import { format } from 'date-fns'
import { useEffect, useState } from 'react'
import { useTranslation } from 'react-i18next'
import Alert from '../components/Alert'
import Loading from '../components/Loading'
import { bookingAPI, machineAPI, userAPI } from '../services/api'

const Admin = () => {
	const { t } = useTranslation()
	const [users, setUsers] = useState([])
	const [bookings, setBookings] = useState([])
	const [machines, setMachines] = useState([])
	const [selectedDate, setSelectedDate] = useState(new Date())
	const [alert, setAlert] = useState({ type: '', message: '' })
	const [loading, setLoading] = useState(false)
	const [auth, setAuth] = useState({ username: '', password: '' })
	const [isAuthenticated, setIsAuthenticated] = useState(false)
	const [newMachineName, setNewMachineName] = useState('')
	const [showAddForm, setShowAddForm] = useState(false)

	useEffect(() => {
		if (isAuthenticated) {
			loadData()
		}
	}, [isAuthenticated, selectedDate])

	const loadData = async () => {
		setLoading(true)
		try {
			const [usersRes, bookingsRes, machinesRes] = await Promise.all([
				userAPI.getAll(),
				bookingAPI.getAll(format(selectedDate, 'yyyy-MM-dd')),
				machineAPI.getAll(),
			])

			setUsers(usersRes.data.data)
			setBookings(bookingsRes.data.data)
			setMachines(machinesRes.data.data)
		} catch (error) {
			showAlert('error', t('error.serverError'))
		} finally {
			setLoading(false)
		}
	}

	const showAlert = (type, message) => {
		setAlert({ type, message })
		setTimeout(() => setAlert({ type: '', message: '' }), 5000)
	}

	const handleLogin = e => {
		e.preventDefault()
		if (auth.username === 'rustam' && auth.password === '12345678') {
			setIsAuthenticated(true)
			showAlert('success', t('admin.loginSuccess'))
		} else {
			showAlert('error', t('admin.invalidCredentials'))
		}
	}

	const handleDeleteBooking = async id => {
		if (!window.confirm(t('admin.deleteConfirmation'))) return

		try {
			await bookingAPI.delete(id)
			showAlert('success', t('booking.cancelSuccess'))
			loadData()
		} catch (error) {
			showAlert('error', t('error.cancelBooking'))
		}
	}

	const handleMachineToggle = async (machineId, isActive) => {
		try {
			await machineAPI.update(machineId, { is_active: !isActive })
			showAlert('success', t('admin.machineStatusUpdated'))
			loadData()
		} catch (error) {
			showAlert('error', t('error.updateMachine'))
		}
	}

	const handleCreateMachine = async e => {
		e.preventDefault()
		if (!newMachineName.trim()) {
			showAlert('error', t('admin.machineNameRequired'))
			return
		}

		try {
			await machineAPI.create({ name: newMachineName })
			showAlert('success', t('admin.machineAdded'))
			setNewMachineName('')
			setShowAddForm(false)
			loadData()
		} catch (error) {
			showAlert('error', t('error.addMachine'))
		}
	}

	const handleDeleteMachine = async machineId => {
		if (!window.confirm(t('admin.machineDeleteConfirmation'))) return

		try {
			await machineAPI.delete(machineId)
			showAlert('success', t('admin.machineDeleted'))
			loadData()
		} catch (error) {
			showAlert('error', t('error.deleteMachine'))
		}
	}

	if (!isAuthenticated) {
		return (
			<div className='container'>
				<div className='form-container'>
					<h2>{t('admin.login')}</h2>
					<Alert type={alert.type} message={alert.message} />
					<form onSubmit={handleLogin}>
						<div className='form-group'>
							<label>{t('admin.username')}</label>
							<input
								type='text'
								value={auth.username}
								onChange={e => setAuth({ ...auth, username: e.target.value })}
								required
							/>
						</div>
						<div className='form-group'>
							<label>{t('admin.password')}</label>
							<input
								type='password'
								value={auth.password}
								onChange={e => setAuth({ ...auth, password: e.target.value })}
								required
							/>
						</div>
						<button type='submit' className='btn'>
							{t('admin.login')}
						</button>
					</form>
				</div>
			</div>
		)
	}

	if (loading) return <Loading />

	return (
		<div className='container'>
			<div className='hero'>
				<h1>{t('admin.title')}</h1>
				<button
					onClick={() => setIsAuthenticated(false)}
					className='btn btn-danger'
					style={{ marginTop: '1rem' }}
				>
					{t('admin.logout')}
				</button>
			</div>

			<Alert type={alert.type} message={alert.message} />

			{/* Statistika */}
			<div className='form-container'>
				<h2>{t('admin.stats')}</h2>
				<div className='stats-grid'>
					<div className='stat-card'>
						<div className='stat-number'>{users.length}</div>
						<div className='stat-label'>{t('admin.registeredUsers')}</div>
					</div>
					<div className='stat-card'>
						<div className='stat-number'>{bookings.length}</div>
						<div className='stat-label'>{t('admin.todayBookings')}</div>
					</div>
					<div className='stat-card'>
						<div className='stat-number'>{machines.length}</div>
						<div className='stat-label'>{t('admin.totalMachines')}</div>
					</div>
				</div>
			</div>

			{/* Kunni tanlash */}
			<div className='form-container'>
				<h2>{t('admin.viewBookings')}</h2>
				<div className='form-group'>
					<label>{t('admin.selectDate')}</label>
					<input
						type='date'
						value={format(selectedDate, 'yyyy-MM-dd')}
						onChange={e => setSelectedDate(new Date(e.target.value))}
					/>
				</div>
			</div>

			{/* Mashinalar boshqaruvi */}
			<div className='form-container'>
				<div className='section-header'>
					<h2>{t('admin.machineManagement')}</h2>
					<button
						onClick={() => setShowAddForm(!showAddForm)}
						className='btn btn-success'
					>
						{showAddForm ? t('admin.cancel') : t('admin.addMachine')}
					</button>
				</div>

				{/* Yangi mashina qo'shish formasi */}
				{showAddForm && (
					<form onSubmit={handleCreateMachine} className='add-machine-form'>
						<h4>{t('admin.addMachine')}</h4>
						<div className='form-group'>
							<label>{t('admin.machineName')}</label>
							<input
								type='text'
								value={newMachineName}
								onChange={e => setNewMachineName(e.target.value)}
								placeholder={t('admin.machineName')}
								required
							/>
						</div>
						<button type='submit' className='btn btn-success'>
							{t('admin.add')}
						</button>
					</form>
				)}

				<div className='table-responsive'>
					<table className='table'>
						<thead>
							<tr>
								<th>{t('admin.machines')}</th>
								<th>{t('admin.status')}</th>
								<th className='actions-column'>{t('common.actions')}</th>
							</tr>
						</thead>
						<tbody>
							{machines.map(machine => (
								<tr key={machine.id}>
									<td className='machine-name'>{machine.name}</td>
									<td>
										<span
											className={`status-badge ${
												machine.is_active ? 'status-active' : 'status-inactive'
											}`}
										>
											{machine.is_active
												? t('admin.active')
												: t('admin.inactive')}
										</span>
									</td>
									<td className='actions-cell'>
										<div className='actions-group'>
											<button
												onClick={() =>
													handleMachineToggle(machine.id, machine.is_active)
												}
												className={`btn ${
													machine.is_active ? 'btn-warning' : 'btn-success'
												}`}
											>
												{machine.is_active
													? t('admin.turnOff')
													: t('admin.turnOn')}
											</button>
											<button
												onClick={() => handleDeleteMachine(machine.id)}
												className='btn btn-danger'
											>
												{t('admin.delete')}
											</button>
										</div>
									</td>
								</tr>
							))}
						</tbody>
					</table>
				</div>
			</div>

			{/* Bronlar */}
			<div className='form-container'>
				<h2>
					{t('admin.bookings')} ({format(selectedDate, 'dd.MM.yyyy')})
				</h2>
				{bookings.length === 0 ? (
					<p className='no-data'>{t('booking.noBookings')}</p>
				) : (
					<div className='table-responsive'>
						<table className='table'>
							<thead>
								<tr>
									<th>{t('register.fullName')}</th>
									<th>{t('common.room')}</th>
									<th>{t('admin.machines')}</th>
									<th>{t('booking.time')}</th>
									<th className='actions-column'>{t('common.actions')}</th>
								</tr>
							</thead>
							<tbody>
								{bookings.map(booking => (
									<tr key={booking.id}>
										<td className='booking-name'>{booking.full_name}</td>
										<td className='booking-room'>{booking.room_number}</td>
										<td className='booking-machine'>{booking.machine_name}</td>
										<td className='booking-time'>{booking.time_slot}</td>
										<td className='actions-cell'>
											<button
												onClick={() => handleDeleteBooking(booking.id)}
												className='btn btn-danger btn-sm'
											>
												{t('common.delete')}
											</button>
										</td>
									</tr>
								))}
							</tbody>
						</table>
					</div>
				)}
			</div>

			{/* Foydalanuvchilar */}
			<div className='form-container'>
				<h2>{t('admin.users')}</h2>
				{users.length === 0 ? (
					<p className='no-data'>{t('admin.noUsers')}</p>
				) : (
					<div className='table-responsive'>
						<table className='table'>
							<thead>
								<tr>
									<th>{t('register.fullName')}</th>
									<th>{t('common.room')}</th>
									<th>{t('booking.bookedDate')}</th>
								</tr>
							</thead>
							<tbody>
								{users.map(user => (
									<tr key={user.id}>
										<td className='user-name'>{user.full_name}</td>
										<td className='user-room'>{user.room_number}</td>
										<td className='user-date'>
											{format(new Date(user.created_at), 'dd.MM.yyyy HH:mm')}
										</td>
									</tr>
								))}
							</tbody>
						</table>
					</div>
				)}
			</div>
		</div>
	)
}

export default Admin
