import { useState } from 'react'
import { userAPI } from '../services/api'
import Alert from './Alert'

const Register = ({ onRegister }) => {
	const [user, setUser] = useState({ full_name: '', room_number: '' })
	const [alert, setAlert] = useState({ type: '', message: '' })
	const [loading, setLoading] = useState(false)
	const [errors, setErrors] = useState({})

	const showAlert = (type, message) => {
		setAlert({ type, message })
		setTimeout(() => setAlert({ type: '', message: '' }), 5000)
	}

	const validateForm = () => {
		const newErrors = {}

		// Ism-familiya validatsiya
		if (!user.full_name.trim()) {
			newErrors.full_name = 'Ism-familiya kiritilishi shart'
		} else if (user.full_name.trim().length < 2) {
			newErrors.full_name =
				"Ism-familiya kamida 2 ta belgidan iborat bo'lishi kerak"
		} else if (user.full_name.trim().length > 100) {
			newErrors.full_name = 'Ism-familiya 100 ta belgidan oshmasligi kerak'
		}

		// Xona raqami validatsiya
		if (!user.room_number.trim()) {
			newErrors.room_number = 'Xona raqami kiritilishi shart'
		} else if (!/^[0-9]{2,4}$/.test(user.room_number.trim())) {
			newErrors.room_number =
				"Xona raqami faqat raqamlardan iborat bo'lishi kerak (2-4 ta)"
		}

		setErrors(newErrors)
		return Object.keys(newErrors).length === 0
	}

	const handleSubmit = async e => {
		e.preventDefault()

		// Client tomonida validatsiya
		if (!validateForm()) {
			showAlert('error', "Iltimos, barcha maydonlarni to'g'ri to'ldiring")
			return
		}

		setLoading(true)

		try {
			await userAPI.register(user)
			showAlert('success', "Muvaffaqiyatli ro'yxatdan o'tdingiz!")
			onRegister(user.room_number)
		} catch (error) {
			showAlert('error', error.response?.data?.message || 'Xatolik yuz berdi')
		} finally {
			setLoading(false)
		}
	}

	const handleInputChange = (field, value) => {
		setUser(prev => ({ ...prev, [field]: value }))

		// Real-time validatsiya
		if (errors[field]) {
			setErrors(prev => ({ ...prev, [field]: '' }))
		}
	}

	return (
		<div className='form-container'>
			<h2>Ro'yxatdan O'tish</h2>
			<Alert type={alert.type} message={alert.message} />
			<form onSubmit={handleSubmit}>
				<div className='form-group'>
					<label>
						Ism Familiya *
						{errors.full_name && (
							<span
								style={{
									color: '#dc3545',
									fontSize: '0.8rem',
									marginLeft: '0.5rem',
								}}
							>
								{errors.full_name}
							</span>
						)}
					</label>
					<input
						type='text'
						value={user.full_name}
						onChange={e => handleInputChange('full_name', e.target.value)}
						required
						placeholder='Ism va familiyangiz'
						style={{
							borderColor: errors.full_name ? '#dc3545' : '#e1e1e1',
							borderWidth: errors.full_name ? '2px' : '1px',
						}}
					/>
				</div>

				<div className='form-group'>
					<label>
						Xona Raqami *
						{errors.room_number && (
							<span
								style={{
									color: '#dc3545',
									fontSize: '0.8rem',
									marginLeft: '0.5rem',
								}}
							>
								{errors.room_number}
							</span>
						)}
					</label>
					<input
						type='text'
						value={user.room_number}
						onChange={e => handleInputChange('room_number', e.target.value)}
						required
						placeholder='Masalan: 101'
						style={{
							borderColor: errors.room_number ? '#dc3545' : '#e1e1e1',
							borderWidth: errors.room_number ? '2px' : '1px',
						}}
					/>
				</div>

				<button type='submit' className='btn' disabled={loading}>
					{loading ? 'Kutilmoqda...' : "Ro'yxatdan O'tish"}
				</button>

				<div
					style={{
						marginTop: '1rem',
						padding: '0.75rem',
						background: '#f8f9fa',
						borderRadius: '5px',
						fontSize: '0.9rem',
						color: '#666',
					}}
				>
					<p style={{ margin: 0 }}>
						<strong>Eslatma:</strong> Ism-familiya va xona raqami kiritish
						majburiy.
					</p>
				</div>
			</form>
		</div>
	)
}

export default Register
