import { format } from 'date-fns'
import { useEffect, useState } from 'react'
import { useTranslation } from 'react-i18next'
import { bookingAPI, machineAPI, userAPI } from '../services/api'

import AdminStats from '../components/AdminStats'
import Alert from '../components/Alert'
import BookingTable from '../components/BookingTable'
import Loading from '../components/Loading'
import LoginForm from '../components/LoginForm'
import MachineTable from '../components/MachineTable'
import UserTable from '../components/UserTable'

const Admin = () => {
	const { t } = useTranslation()
	const [users, setUsers] = useState([])
	const [bookings, setBookings] = useState([])
	const [machines, setMachines] = useState([])
	const [selectedDate, setSelectedDate] = useState(new Date())
	const [alert, setAlert] = useState({ type: '', message: '' })
	const [loading, setLoading] = useState(false)
	const [auth, setAuth] = useState({ username: '', password: '' })
	const [isAuthenticated, setIsAuthenticated] = useState(false)
	const [newMachineName, setNewMachineName] = useState('')
	const [showAddForm, setShowAddForm] = useState(false)

	useEffect(() => {
		if (isAuthenticated) loadData()
	}, [isAuthenticated, selectedDate])

	const loadData = async () => {
		setLoading(true)
		try {
			const [usersRes, bookingsRes, machinesRes] = await Promise.all([
				userAPI.getAll(),
				bookingAPI.getAll(format(selectedDate, 'yyyy-MM-dd')),
				machineAPI.getAll(),
			])
			setUsers(usersRes.data.data)
			setBookings(bookingsRes.data.data)
			setMachines(machinesRes.data.data)
		} catch (err) {
			showAlert('error', t('error.serverError'))
		} finally {
			setLoading(false)
		}
	}

	const showAlert = (type, message) => {
		setAlert({ type, message })
		setTimeout(() => setAlert({ type: '', message: '' }), 4000)
	}

	const handleLogin = e => {
		e.preventDefault()
		if (auth.username === 'rustam' && auth.password === '12345678') {
			setIsAuthenticated(true)
			showAlert('success', t('admin.loginSuccess'))
		} else {
			showAlert('error', t('admin.invalidCredentials'))
		}
	}

	const handleDeleteBooking = async id => {
		if (!window.confirm(t('admin.deleteConfirmation'))) return
		try {
			await bookingAPI.delete(id)
			showAlert('success', t('booking.cancelSuccess'))
			loadData()
		} catch {
			showAlert('error', t('error.cancelBooking'))
		}
	}

	const handleMachineToggle = async (id, isActive) => {
		try {
			await machineAPI.update(id, { is_active: !isActive })
			showAlert('success', t('admin.machineStatusUpdated'))
			loadData()
		} catch {
			showAlert('error', t('error.updateMachine'))
		}
	}

	const handleCreateMachine = async e => {
		e.preventDefault()
		if (!newMachineName.trim())
			return showAlert('error', t('admin.machineNameRequired'))
		try {
			await machineAPI.create({ name: newMachineName })
			showAlert('success', t('admin.machineAdded'))
			setNewMachineName('')
			setShowAddForm(false)
			loadData()
		} catch {
			showAlert('error', t('error.addMachine'))
		}
	}

	const handleDeleteMachine = async id => {
		if (!window.confirm(t('admin.machineDeleteConfirmation'))) return
		try {
			await machineAPI.delete(id)
			showAlert('success', t('admin.machineDeleted'))
			loadData()
		} catch {
			showAlert('error', t('error.deleteMachine'))
		}
	}

	if (!isAuthenticated) {
		return (
			<div className='min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-500 to-purple-600 p-6'>
				<LoginForm
					auth={auth}
					setAuth={setAuth}
					handleLogin={handleLogin}
					alert={alert}
					t={t}
				/>
			</div>
		)
	}

	if (loading) return <Loading />

	return (
		<div className='min-h-screen bg-gradient-to-br from-indigo-50 to-purple-100 py-10 px-4 sm:px-6 lg:px-8'>
			<div className='max-w-7xl mx-auto space-y-10'>
				{/* Header */}
				<div className='text-center space-y-2'>
					<h1 className='text-3xl sm:text-4xl font-bold text-gray-800'>
						{t('admin.title')}
					</h1>
					<button
						onClick={() => setIsAuthenticated(false)}
						className='mt-3 inline-block bg-red-500 hover:bg-red-600 text-white px-5 py-2 rounded-lg font-semibold transition'
					>
						{t('admin.logout')}
					</button>
				</div>

				{/* Alert */}
				<Alert type={alert.type} message={alert.message} />

				{/* Stats */}
				<AdminStats
					users={users.length}
					bookings={bookings.length}
					machines={machines.length}
					t={t}
				/>

				{/* Date Picker */}
				<div className='bg-white rounded-xl shadow-md p-5'>
					<h2 className='text-lg font-semibold text-gray-700 mb-3'>
						{t('admin.viewBookings')}
					</h2>
					<input
						type='date'
						value={format(selectedDate, 'yyyy-MM-dd')}
						onChange={e => setSelectedDate(new Date(e.target.value))}
						className='border border-gray-300 rounded-md p-2 w-full sm:w-auto focus:ring-2 focus:ring-indigo-400 outline-none'
					/>
				</div>

				{/* Machine Management */}
				<MachineTable
					machines={machines}
					t={t}
					showAddForm={showAddForm}
					setShowAddForm={setShowAddForm}
					newMachineName={newMachineName}
					setNewMachineName={setNewMachineName}
					handleCreateMachine={handleCreateMachine}
					handleMachineToggle={handleMachineToggle}
					handleDeleteMachine={handleDeleteMachine}
				/>

				{/* Bookings */}
				<BookingTable
					bookings={bookings}
					selectedDate={selectedDate}
					t={t}
					handleDeleteBooking={handleDeleteBooking}
				/>

				{/* Users */}
				<UserTable users={users} t={t} />
			</div>
		</div>
	)
}

export default Admin
