import { format } from 'date-fns'
import { useCallback, useEffect, useState } from 'react'
import { useTranslation } from 'react-i18next'
import '../App.css'
import Alert from '../components/Alert'
import Loading from '../components/Loading'
import { bookingAPI, machineAPI, userAPI } from '../services/api'

const Admin = () => {
	const { t } = useTranslation()
	const [users, setUsers] = useState([])
	const [bookings, setBookings] = useState([])
	const [machines, setMachines] = useState([])
	const [selectedDate, setSelectedDate] = useState(new Date())
	const [alert, setAlert] = useState({ type: '', message: '' })
	const [loading, setLoading] = useState(false)
	const [auth, setAuth] = useState({ username: '', password: '' })
	const [isAuthenticated, setIsAuthenticated] = useState(false)
	const [currentPage, setCurrentPage] = useState({
		users: 1,
		bookings: 1,
		machines: 1,
	})
	const [searchTerm, setSearchTerm] = useState({
		users: '',
		bookings: '',
		machines: '',
	})
	const [sortConfig, setSortConfig] = useState({ key: '', direction: '' })

	const ITEMS_PER_PAGE = 10

	// Authentication check from localStorage
	useEffect(() => {
		const savedAuth = localStorage.getItem('adminAuth')
		if (savedAuth) {
			setIsAuthenticated(true)
		}
	}, [])

	// Load data when authenticated or date changes
	useEffect(() => {
		if (isAuthenticated) {
			loadData()
		}
	}, [isAuthenticated, selectedDate])

	const loadData = useCallback(async () => {
		setLoading(true)
		try {
			const [usersRes, bookingsRes, machinesRes] = await Promise.all([
				userAPI.getAll(),
				bookingAPI.getAll(format(selectedDate, 'yyyy-MM-dd')),
				machineAPI.getAll(),
			])

			setUsers(usersRes.data?.data || [])
			setBookings(bookingsRes.data?.data || [])
			setMachines(machinesRes.data?.data || [])
		} catch (error) {
			console.error('Data loading error:', error)
			showAlert('error', t('error.serverError'))
		} finally {
			setLoading(false)
		}
	}, [selectedDate, t])

	const showAlert = useCallback((type, message) => {
		setAlert({ type, message })
		setTimeout(() => setAlert({ type: '', message: '' }), 4000)
	}, [])

	const handleLogin = e => {
		e.preventDefault()
		// In production, this should be an API call to backend
		if (
			auth.username === process.env.REACT_APP_ADMIN_USERNAME &&
			auth.password === process.env.REACT_APP_ADMIN_PASSWORD
		) {
			setIsAuthenticated(true)
			localStorage.setItem('adminAuth', 'true')
			showAlert('success', t('admin.loginSuccess'))
		} else {
			showAlert('error', t('admin.invalidCredentials'))
		}
	}

	const handleLogout = () => {
		setIsAuthenticated(false)
		localStorage.removeItem('adminAuth')
		setAuth({ username: '', password: '' })
	}

	const handleDeleteBooking = async id => {
		if (!window.confirm(t('admin.deleteConfirmation'))) return
		try {
			await bookingAPI.delete(id)
			showAlert('success', t('booking.cancelSuccess'))
			loadData()
		} catch {
			showAlert('error', t('error.cancelBooking'))
		}
	}

	const handleMachineToggle = async (id, isActive) => {
		try {
			await machineAPI.update(id, { is_active: !isActive })
			showAlert('success', t('admin.machineStatusUpdated'))
			loadData()
		} catch {
			showAlert('error', t('error.updateMachine'))
		}
	}

	// Filter and search functions
	const filterData = (data, type) => {
		let filtered = data
		const search = searchTerm[type].toLowerCase()

		if (search) {
			filtered = filtered.filter(item =>
				Object.values(item).some(value =>
					String(value).toLowerCase().includes(search)
				)
			)
		}

		// Sorting
		if (sortConfig.key && sortConfig.direction) {
			filtered = [...filtered].sort((a, b) => {
				const aValue = a[sortConfig.key]
				const bValue = b[sortConfig.key]

				if (aValue < bValue) {
					return sortConfig.direction === 'asc' ? -1 : 1
				}
				if (aValue > bValue) {
					return sortConfig.direction === 'asc' ? 1 : -1
				}
				return 0
			})
		}

		return filtered
	}

	// Pagination functions
	const getPaginatedData = (data, type) => {
		const startIndex = (currentPage[type] - 1) * ITEMS_PER_PAGE
		return data.slice(startIndex, startIndex + ITEMS_PER_PAGE)
	}

	const handleSort = key => {
		setSortConfig(prev => ({
			key,
			direction: prev.key === key && prev.direction === 'asc' ? 'desc' : 'asc',
		}))
	}

	const SortIcon = ({ columnKey }) => {
		if (sortConfig.key !== columnKey) return '↕️'
		return sortConfig.direction === 'asc' ? '↑' : '↓'
	}

	// Login page
	if (!isAuthenticated) {
		return (
			<div className='admin-login'>
				<form onSubmit={handleLogin} className='admin-login-form'>
					<h2>{t('admin.login')}</h2>
					<Alert type={alert.type} message={alert.message} />
					<div className='form-group'>
						<label>{t('admin.username')}</label>
						<input
							type='text'
							value={auth.username}
							onChange={e => setAuth({ ...auth, username: e.target.value })}
							required
							autoComplete='username'
						/>
					</div>
					<div className='form-group'>
						<label>{t('admin.password')}</label>
						<input
							type='password'
							value={auth.password}
							onChange={e => setAuth({ ...auth, password: e.target.value })}
							required
							autoComplete='current-password'
						/>
					</div>
					<button type='submit' className='btn-primary'>
						{t('admin.login')}
					</button>
				</form>
			</div>
		)
	}

	if (loading) return <Loading />

	const filteredUsers = filterData(users, 'users')
	const filteredBookings = filterData(bookings, 'bookings')
	const filteredMachines = filterData(machines, 'machines')

	const paginatedUsers = getPaginatedData(filteredUsers, 'users')
	const paginatedBookings = getPaginatedData(filteredBookings, 'bookings')
	const paginatedMachines = getPaginatedData(filteredMachines, 'machines')

	return (
		<div className='admin-container'>
			<header className='admin-header'>
				<div className='header-content'>
					<h1>{t('admin.title')}</h1>
					<div className='header-actions'>
						<span className='welcome-text'>
							{t('admin.welcome')}, {process.env.REACT_APP_ADMIN_USERNAME}
						</span>
						<button
							onClick={handleLogout}
							className='btn-danger logout-btn'
							aria-label={t('admin.logout')}
						>
							{t('admin.logout')}
						</button>
					</div>
				</div>
			</header>

			<Alert type={alert.type} message={alert.message} />

			{/* Statistics Section */}
			<section className='stats-section'>
				<div className='stat-card'>
					<span className='stat-number'>{users.length}</span>
					<p>{t('admin.registeredUsers')}</p>
				</div>
				<div className='stat-card'>
					<span className='stat-number'>{bookings.length}</span>
					<p>{t('admin.todayBookings')}</p>
				</div>
				<div className='stat-card'>
					<span className='stat-number'>{machines.length}</span>
					<p>{t('admin.totalMachines')}</p>
				</div>
				<div className='stat-card'>
					<span className='stat-number'>
						{bookings.filter(b => b.status === 'active').length}
					</span>
					<p>{t('admin.activeBookings')}</p>
				</div>
			</section>

			{/* Date Selection */}
			<section className='date-section'>
				<div className='section-header'>
					<h2>{t('admin.dateFilter')}</h2>
				</div>
				<div className='date-controls'>
					<label>{t('admin.selectDate')}</label>
					<input
						type='date'
						value={format(selectedDate, 'yyyy-MM-dd')}
						onChange={e => setSelectedDate(new Date(e.target.value))}
						max={format(new Date(), 'yyyy-MM-dd')}
					/>
				</div>
			</section>

			{/* Machine Management */}
			<section className='machine-section'>
				<div className='section-header'>
					<h2>{t('admin.machineManagement')}</h2>
					<div className='search-box'>
						<input
							type='text'
							placeholder={t('admin.searchMachines')}
							value={searchTerm.machines}
							onChange={e =>
								setSearchTerm(prev => ({ ...prev, machines: e.target.value }))
							}
						/>
					</div>
				</div>

				<div className='table-container'>
					<table className='data-table'>
						<thead>
							<tr>
								<th onClick={() => handleSort('name')} className='sortable'>
									{t('admin.machines')} <SortIcon columnKey='name' />
								</th>
								<th
									onClick={() => handleSort('is_active')}
									className='sortable'
								>
									{t('admin.status')} <SortIcon columnKey='is_active' />
								</th>
								<th>{t('common.actions')}</th>
							</tr>
						</thead>
						<tbody>
							{paginatedMachines.map(machine => (
								<tr key={machine.id}>
									<td>{machine.name}</td>
									<td>
										<span
											className={`status ${
												machine.is_active ? 'status-active' : 'status-inactive'
											}`}
										>
											{machine.is_active
												? t('admin.active')
												: t('admin.inactive')}
										</span>
									</td>
									<td>
										<button
											onClick={() =>
												handleMachineToggle(machine.id, machine.is_active)
											}
											className={
												machine.is_active ? 'btn-warning' : 'btn-success'
											}
											aria-label={
												machine.is_active
													? t('admin.turnOff')
													: t('admin.turnOn')
											}
										>
											{machine.is_active
												? t('admin.turnOff')
												: t('admin.turnOn')}
										</button>
									</td>
								</tr>
							))}
						</tbody>
					</table>

					{/* Pagination */}
					{filteredMachines.length > ITEMS_PER_PAGE && (
						<div className='pagination'>
							<button
								disabled={currentPage.machines === 1}
								onClick={() =>
									setCurrentPage(prev => ({
										...prev,
										machines: prev.machines - 1,
									}))
								}
							>
								{t('common.previous')}
							</button>
							<span>
								{t('admin.page')} {currentPage.machines} {t('admin.of')}{' '}
								{Math.ceil(filteredMachines.length / ITEMS_PER_PAGE)}
							</span>
							<button
								disabled={
									currentPage.machines ===
									Math.ceil(filteredMachines.length / ITEMS_PER_PAGE)
								}
								onClick={() =>
									setCurrentPage(prev => ({
										...prev,
										machines: prev.machines + 1,
									}))
								}
							>
								{t('common.next')}
							</button>
						</div>
					)}
				</div>
			</section>

			{/* Bookings Section */}
			<section className='bookings-section'>
				<div className='section-header'>
					<h2>
						{t('admin.bookings')} ({format(selectedDate, 'dd.MM.yyyy')})
					</h2>
					<div className='search-box'>
						<input
							type='text'
							placeholder={t('admin.searchBookings')}
							value={searchTerm.bookings}
							onChange={e =>
								setSearchTerm(prev => ({ ...prev, bookings: e.target.value }))
							}
						/>
					</div>
				</div>

				<div className='table-container'>
					{filteredBookings.length ? (
						<>
							<table className='data-table'>
								<thead>
									<tr>
										<th
											onClick={() => handleSort('full_name')}
											className='sortable'
										>
											{t('register.fullName')}{' '}
											<SortIcon columnKey='full_name' />
										</th>
										<th
											onClick={() => handleSort('room_number')}
											className='sortable'
										>
											{t('common.room')} <SortIcon columnKey='room_number' />
										</th>
										<th
											onClick={() => handleSort('machine_name')}
											className='sortable'
										>
											{t('admin.machines')}{' '}
											<SortIcon columnKey='machine_name' />
										</th>
										<th
											onClick={() => handleSort('time_slot')}
											className='sortable'
										>
											{t('booking.time')} <SortIcon columnKey='time_slot' />
										</th>
										<th>{t('common.actions')}</th>
									</tr>
								</thead>
								<tbody>
									{paginatedBookings.map(booking => (
										<tr key={booking.id}>
											<td>{booking.full_name}</td>
											<td>{booking.room_number}</td>
											<td>{booking.machine_name}</td>
											<td>{booking.time_slot}</td>
											<td>
												<button
													onClick={() => handleDeleteBooking(booking.id)}
													className='btn-danger btn-sm'
													aria-label={`${t('common.delete')} ${
														booking.full_name
													} booking`}
												>
													{t('common.delete')}
												</button>
											</td>
										</tr>
									))}
								</tbody>
							</table>

							{/* Pagination */}
							{filteredBookings.length > ITEMS_PER_PAGE && (
								<div className='pagination'>
									<button
										disabled={currentPage.bookings === 1}
										onClick={() =>
											setCurrentPage(prev => ({
												...prev,
												bookings: prev.bookings - 1,
											}))
										}
									>
										{t('common.previous')}
									</button>
									<span>
										{t('admin.page')} {currentPage.bookings} {t('admin.of')}{' '}
										{Math.ceil(filteredBookings.length / ITEMS_PER_PAGE)}
									</span>
									<button
										disabled={
											currentPage.bookings ===
											Math.ceil(filteredBookings.length / ITEMS_PER_PAGE)
										}
										onClick={() =>
											setCurrentPage(prev => ({
												...prev,
												bookings: prev.bookings + 1,
											}))
										}
									>
										{t('common.next')}
									</button>
								</div>
							)}
						</>
					) : (
						<p className='no-data'>{t('booking.noBookings')}</p>
					)}
				</div>
			</section>

			{/* Users Section */}
			<section className='users-section'>
				<div className='section-header'>
					<h2>{t('admin.users')}</h2>
					<div className='search-box'>
						<input
							type='text'
							placeholder={t('admin.searchUsers')}
							value={searchTerm.users}
							onChange={e =>
								setSearchTerm(prev => ({ ...prev, users: e.target.value }))
							}
						/>
					</div>
				</div>

				<div className='table-container'>
					{filteredUsers.length ? (
						<>
							<table className='data-table'>
								<thead>
									<tr>
										<th
											onClick={() => handleSort('full_name')}
											className='sortable'
										>
											{t('register.fullName')}{' '}
											<SortIcon columnKey='full_name' />
										</th>
										<th
											onClick={() => handleSort('room_number')}
											className='sortable'
										>
											{t('common.room')} <SortIcon columnKey='room_number' />
										</th>
										<th
											onClick={() => handleSort('created_at')}
											className='sortable'
										>
											{t('booking.bookedDate')}{' '}
											<SortIcon columnKey='created_at' />
										</th>
									</tr>
								</thead>
								<tbody>
									{paginatedUsers.map(user => (
										<tr key={user.id}>
											<td>{user.full_name}</td>
											<td>{user.room_number}</td>
											<td>
												{format(new Date(user.created_at), 'dd.MM.yyyy HH:mm')}
											</td>
										</tr>
									))}
								</tbody>
							</table>

							{/* Pagination */}
							{filteredUsers.length > ITEMS_PER_PAGE && (
								<div className='pagination'>
									<button
										disabled={currentPage.users === 1}
										onClick={() =>
											setCurrentPage(prev => ({
												...prev,
												users: prev.users - 1,
											}))
										}
									>
										{t('common.previous')}
									</button>
									<span>
										{t('admin.page')} {currentPage.users} {t('admin.of')}{' '}
										{Math.ceil(filteredUsers.length / ITEMS_PER_PAGE)}
									</span>
									<button
										disabled={
											currentPage.users ===
											Math.ceil(filteredUsers.length / ITEMS_PER_PAGE)
										}
										onClick={() =>
											setCurrentPage(prev => ({
												...prev,
												users: prev.users + 1,
											}))
										}
									>
										{t('common.next')}
									</button>
								</div>
							)}
						</>
					) : (
						<p className='no-data'>{t('admin.noUsers')}</p>
					)}
				</div>
			</section>
		</div>
	)
}

export default Admin
