import { format } from 'date-fns'
import { useEffect, useState } from 'react'
import Alert from '../components/Alert'
import Loading from '../components/Loading'
import { bookingAPI, machineAPI, userAPI } from '../services/api'

const Admin = () => {
	const [users, setUsers] = useState([])
	const [bookings, setBookings] = useState([])
	const [machines, setMachines] = useState([])
	const [selectedDate, setSelectedDate] = useState(new Date())
	const [alert, setAlert] = useState({ type: '', message: '' })
	const [loading, setLoading] = useState(false)
	const [auth, setAuth] = useState({ username: '', password: '' })
	const [isAuthenticated, setIsAuthenticated] = useState(false)
	const [newMachineName, setNewMachineName] = useState('')
	const [showAddForm, setShowAddForm] = useState(false)

	useEffect(() => {
		if (isAuthenticated) {
			loadData()
		}
	}, [isAuthenticated, selectedDate])

	const loadData = async () => {
		setLoading(true)
		try {
			const [usersRes, bookingsRes, machinesRes] = await Promise.all([
				userAPI.getAll(),
				bookingAPI.getAll(format(selectedDate, 'yyyy-MM-dd')),
				machineAPI.getAll(),
			])

			setUsers(usersRes.data.data)
			setBookings(bookingsRes.data.data)
			setMachines(machinesRes.data.data)
		} catch (error) {
			showAlert('error', "Ma'lumotlarni yuklashda xatolik")
		} finally {
			setLoading(false)
		}
	}

	const showAlert = (type, message) => {
		setAlert({ type, message })
		setTimeout(() => setAlert({ type: '', message: '' }), 5000)
	}

	const handleLogin = e => {
		e.preventDefault()
		if (auth.username === 'rustam' && auth.password === '12345678') {
			setIsAuthenticated(true)
			showAlert('success', 'Muvaffaqiyatli kirildi')
		} else {
			showAlert('error', "Noto'g'ri login yoki parol")
		}
	}

	const handleDeleteBooking = async id => {
		if (!window.confirm("Bronni o'chirishni istaysizmi?")) return

		try {
			await bookingAPI.delete(id)
			showAlert('success', "Bron muvaffaqiyatli o'chirildi")
			loadData()
		} catch (error) {
			showAlert('error', "Bronni o'chirishda xatolik")
		}
	}

	const handleMachineToggle = async (machineId, isActive) => {
		try {
			await machineAPI.update(machineId, { is_active: !isActive })
			showAlert('success', 'Mashina holati yangilandi')
			loadData()
		} catch (error) {
			showAlert('error', 'Mashinani yangilashda xatolik')
		}
	}

	const handleCreateMachine = async e => {
		e.preventDefault()
		if (!newMachineName.trim()) {
			showAlert('error', 'Mashina nomini kiriting')
			return
		}

		try {
			await machineAPI.create({ name: newMachineName })
			showAlert('success', "Mashina muvaffaqiyatli qo'shildi")
			setNewMachineName('')
			setShowAddForm(false)
			loadData()
		} catch (error) {
			showAlert('error', "Mashina qo'shishda xatolik")
		}
	}

	const handleDeleteMachine = async machineId => {
		if (
			!window.confirm(
				"Mashinani o'chirishni istaysizmi? Bu barcha bronlarni o'chiradi!"
			)
		)
			return

		try {
			await machineAPI.delete(machineId)
			showAlert('success', "Mashina muvaffaqiyatli o'chirildi")
			loadData()
		} catch (error) {
			showAlert('error', "Mashinani o'chirishda xatolik")
		}
	}

	if (!isAuthenticated) {
		return (
			<div className='container'>
				<div className='form-container'>
					<h2>Admin Kirish</h2>
					<Alert type={alert.type} message={alert.message} />
					<form onSubmit={handleLogin}>
						<div className='form-group'>
							<label>Login</label>
							<input
								type='text'
								value={auth.username}
								onChange={e => setAuth({ ...auth, username: e.target.value })}
								required
								placeholder='admin'
							/>
						</div>
						<div className='form-group'>
							<label>Parol</label>
							<input
								type='password'
								value={auth.password}
								onChange={e => setAuth({ ...auth, password: e.target.value })}
								required
								placeholder='password'
							/>
						</div>
						<button type='submit' className='btn'>
							Kirish
						</button>
					</form>
				</div>
			</div>
		)
	}

	if (loading) return <Loading />

	return (
		<div className='container'>
			<div className='hero'>
				<h1>Admin Panel</h1>
				<button
					onClick={() => setIsAuthenticated(false)}
					className='btn btn-danger'
					style={{ marginTop: '1rem' }}
				>
					Chiqish
				</button>
			</div>

			<Alert type={alert.type} message={alert.message} />

			{/* Statistika */}
			<div className='form-container'>
				<h2>Statistika</h2>
				<div
					style={{
						display: 'grid',
						gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
						gap: '1rem',
						marginBottom: '2rem',
					}}
				>
					<div
						style={{
							background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
							color: 'white',
							padding: '1.5rem',
							borderRadius: '10px',
							textAlign: 'center',
						}}
					>
						<div
							style={{
								fontSize: '2rem',
								fontWeight: 'bold',
								marginBottom: '0.5rem',
							}}
						>
							{users.length}
						</div>
						<div style={{ fontSize: '0.9rem', opacity: '0.9' }}>
							Ro'yxatdan O'tganlar
						</div>
					</div>
					<div
						style={{
							background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
							color: 'white',
							padding: '1.5rem',
							borderRadius: '10px',
							textAlign: 'center',
						}}
					>
						<div
							style={{
								fontSize: '2rem',
								fontWeight: 'bold',
								marginBottom: '0.5rem',
							}}
						>
							{bookings.length}
						</div>
						<div style={{ fontSize: '0.9rem', opacity: '0.9' }}>
							Bugungi Bronlar
						</div>
					</div>
					<div
						style={{
							background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
							color: 'white',
							padding: '1.5rem',
							borderRadius: '10px',
							textAlign: 'center',
						}}
					>
						<div
							style={{
								fontSize: '2rem',
								fontWeight: 'bold',
								marginBottom: '0.5rem',
							}}
						>
							{machines.length}
						</div>
						<div style={{ fontSize: '0.9rem', opacity: '0.9' }}>Mashinalar</div>
					</div>
				</div>
			</div>

			{/* Kunni tanlash */}
			<div className='form-container'>
				<h2>Bronlarni Ko'rish</h2>
				<div className='form-group'>
					<label>Kunni tanlang</label>
					<input
						type='date'
						value={format(selectedDate, 'yyyy-MM-dd')}
						onChange={e => setSelectedDate(new Date(e.target.value))}
					/>
				</div>
			</div>

			{/* Mashinalar boshqaruvi */}
			<div className='form-container'>
				<div
					style={{
						display: 'flex',
						justifyContent: 'space-between',
						alignItems: 'center',
						marginBottom: '1.5rem',
					}}
				>
					<h2>Mashinalar Boshqaruvi</h2>
					<button
						onClick={() => setShowAddForm(!showAddForm)}
						className='btn'
						style={{ background: '#28a745' }}
					>
						{showAddForm ? 'Bekor qilish' : '+ Yangi Mashina'}
					</button>
				</div>

				{/* Yangi mashina qo'shish formasi */}
				{showAddForm && (
					<form
						onSubmit={handleCreateMachine}
						style={{
							background: '#f8f9fa',
							padding: '1.5rem',
							borderRadius: '5px',
							marginBottom: '1.5rem',
							border: '1px solid #dee2e6',
						}}
					>
						<h4>Yangi Mashina Qo'shish</h4>
						<div className='form-group'>
							<label>Mashina Nomi</label>
							<input
								type='text'
								value={newMachineName}
								onChange={e => setNewMachineName(e.target.value)}
								placeholder='Masalan: Mashina 6'
								required
							/>
						</div>
						<button
							type='submit'
							className='btn'
							style={{ background: '#28a745' }}
						>
							Qo'shish
						</button>
					</form>
				)}

				<table className='table'>
					<thead>
						<tr>
							<th>Mashina</th>
							<th>Holati</th>
							<th>Amallar</th>
						</tr>
					</thead>
					<tbody>
						{machines.map(machine => (
							<tr key={machine.id}>
								<td>{machine.name}</td>
								<td>
									<span
										className={
											machine.is_active ? 'status-available' : 'status-booked'
										}
									>
										{machine.is_active ? 'Faol' : 'Nofaol'}
									</span>
								</td>
								<td>
									<div style={{ display: 'flex', gap: '0.5rem' }}>
										<button
											onClick={() =>
												handleMachineToggle(machine.id, machine.is_active)
											}
											className={`btn ${machine.is_active ? 'btn-danger' : ''}`}
											style={{ padding: '0.5rem 1rem', fontSize: '0.9rem' }}
										>
											{machine.is_active ? "O'chirish" : 'Yoqish'}
										</button>
										<button
											onClick={() => handleDeleteMachine(machine.id)}
											className='btn btn-danger'
											style={{ padding: '0.5rem 1rem', fontSize: '0.9rem' }}
										>
											Butunlay O'chirish
										</button>
									</div>
								</td>
							</tr>
						))}
					</tbody>
				</table>
			</div>

			{/* Bronlar */}
			<div className='form-container'>
				<h2>Bronlar ({format(selectedDate, 'dd.MM.yyyy')})</h2>
				{bookings.length === 0 ? (
					<p>Bu kunda bronlar mavjud emas</p>
				) : (
					<table className='table'>
						<thead>
							<tr>
								<th>Ism</th>
								<th>Xona</th>
								<th>Mashina</th>
								<th>Vaqt</th>
								<th>Amallar</th>
							</tr>
						</thead>
						<tbody>
							{bookings.map(booking => (
								<tr key={booking.id}>
									<td>{booking.full_name}</td>
									<td>{booking.room_number}</td>
									<td>{booking.machine_name}</td>
									<td>{booking.time_slot}</td>
									<td>
										<button
											onClick={() => handleDeleteBooking(booking.id)}
											className='btn btn-danger'
											style={{ padding: '0.5rem 1rem', fontSize: '0.9rem' }}
										>
											O'chirish
										</button>
									</td>
								</tr>
							))}
						</tbody>
					</table>
				)}
			</div>

			{/* Foydalanuvchilar */}
			<div className='form-container'>
				<h2>Ro'yxatdan O'tganlar</h2>
				{users.length === 0 ? (
					<p>Ro'yxatdan o'tganlar mavjud emas</p>
				) : (
					<table className='table'>
						<thead>
							<tr>
								<th>Ism</th>
								<th>Xona</th>
								<th>Ro'yxatdan o'tgan sana</th>
							</tr>
						</thead>
						<tbody>
							{users.map(user => (
								<tr key={user.id}>
									<td>{user.full_name}</td>
									<td>{user.room_number}</td>
									<td>
										{format(new Date(user.created_at), 'dd.MM.yyyy HH:mm')}
									</td>
								</tr>
							))}
						</tbody>
					</table>
				)}
			</div>
		</div>
	)
}

export default Admin
