// ESKI KOD (o'chirish):
;<div className='booking-grid'>
	{timeSlots.map(timeSlot => (
		<div key={timeSlot} className='time-slot'>
			<h3>{timeSlot}</h3>
			<div className='machine-list'>
				{machines.map(machine => {
					// ... mashinalar
				})}
			</div>
		</div>
	))}
</div>

// YANGI KOD (almashtirish):
{
	selectedTimeSlot ? (
		<div className='time-slot'>
			<h3>{selectedTimeSlot}</h3>
			<div className='machine-list'>
				{machines.map(machine => {
					const isBooked = isMachineBooked(machine.id, selectedTimeSlot)
					const isSelected = selectedMachines.includes(machine.id)

					return (
						<div key={machine.id} className='machine-item'>
							<div className='machine-info'>
								<input
									type='checkbox'
									className='machine-checkbox'
									checked={isSelected}
									onChange={() => {
										if (!isBooked) {
											handleMachineSelect(machine.id)
										}
									}}
									disabled={isBooked}
								/>
								<span>{machine.name}</span>
							</div>
							<div
								className={`machine-status ${
									isBooked ? 'status-booked' : 'status-available'
								}`}
							>
								{isBooked ? 'Band' : "Bo'sh"}
							</div>
						</div>
					)
				})}
			</div>
		</div>
	) : (
		<div
			style={{
				textAlign: 'center',
				padding: '2rem',
				background: '#f8f9fa',
				borderRadius: '5px',
				border: '1px dashed #dee2e6',
			}}
		>
			<p>Vaqt oralig'ini tanlang</p>
			<div
				style={{
					display: 'flex',
					justifyContent: 'center',
					gap: '1rem',
					marginTop: '1rem',
				}}
			>
				{timeSlots.map(slot => (
					<button
						key={slot}
						onClick={() => setSelectedTimeSlot(slot)}
						className='btn'
						style={{
							background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
							color: 'white',
						}}
					>
						{slot}
					</button>
				))}
			</div>
		</div>
	)
}
