import { useTranslation } from 'react-i18next'
import { Link } from 'react-router-dom'

const Header = () => {
	const { t, i18n } = useTranslation()
	const userRoom = localStorage.getItem('userRoom')

	const changeLanguage = lng => {
		i18n.changeLanguage(lng)
	}

	return (
		<header className='header'>
			<div className='container'>
				<div className='header-content'>
					<div className='logo'>{t('header.title')}</div>
					<nav className='nav'>
						<Link to='/'>{t('header.home')}</Link>
						<Link to='/admin'>{t('header.admin')}</Link>

						{/* Til almashtirish tugmalari */}
						<div style={{ display: 'flex', gap: '0.5rem', marginLeft: '2rem' }}>
							<button
								onClick={() => changeLanguage('uz')}
								style={{
									background: i18n.language === 'uz' ? '#fff' : 'transparent',
									color: i18n.language === 'uz' ? '#667eea' : '#fff',
									border: '1px solid #fff',
									padding: '0.25rem 0.5rem',
									borderRadius: '4px',
									cursor: 'pointer',
									fontSize: '0.8rem',
								}}
							>
								UZ
							</button>
							<button
								onClick={() => changeLanguage('en')}
								style={{
									background: i18n.language === 'en' ? '#fff' : 'transparent',
									color: i18n.language === 'en' ? '#667eea' : '#fff',
									border: '1px solid #fff',
									padding: '0.25rem 0.5rem',
									borderRadius: '4px',
									cursor: 'pointer',
									fontSize: '0.8rem',
								}}
							>
								EN
							</button>
							<button
								onClick={() => changeLanguage('ru')}
								style={{
									background: i18n.language === 'ru' ? '#fff' : 'transparent',
									color: i18n.language === 'ru' ? '#667eea' : '#fff',
									border: '1px solid #fff',
									padding: '0.25rem 0.5rem',
									borderRadius: '4px',
									cursor: 'pointer',
									fontSize: '0.8rem',
								}}
							>
								RU
							</button>
						</div>

						{/*userRoom && (
							<span style={{ color: 'white', marginLeft: '2rem' }}>
								{t('common.room')}: {userRoom}
							</span>
						)*/}
					</nav>
				</div>
			</div>
		</header>
	)
}

export default Header
