import { addDays, format, isBefore, startOfWeek } from 'date-fns'
import { useEffect, useState } from 'react'
import Alert from '../components/Alert'
import { bookingAPI, machineAPI, userAPI } from '../services/api'

const Home = () => {
	const [user, setUser] = useState({ full_name: '', room_number: '' })
	const [machines, setMachines] = useState([])
	const [selectedDate, setSelectedDate] = useState(new Date())
	const [selectedTimeSlot, setSelectedTimeSlot] = useState('')
	const [selectedMachines, setSelectedMachines] = useState([])
	const [alert, setAlert] = useState({ type: '', message: '' })
	const [loading, setLoading] = useState(false)
	const [bookings, setBookings] = useState([])

	const timeSlots = ['15:00-16:00', '16:00-17:00', '17:00-18:00']

	useEffect(() => {
		loadMachines()
		loadBookings()
	}, [selectedDate])

	const loadMachines = async () => {
		try {
			const response = await machineAPI.getAll()
			setMachines(response.data.data)
		} catch (error) {
			showAlert('error', 'Mashinalarni yuklashda xatolik')
		}
	}

	const loadBookings = async () => {
		try {
			const dateStr = format(selectedDate, 'yyyy-MM-dd')
			const response = await bookingAPI.getAll(dateStr)
			setBookings(response.data.data)
		} catch (error) {
			console.error('Bookings load error:', error)
		}
	}

	const showAlert = (type, message) => {
		setAlert({ type, message })
		setTimeout(() => setAlert({ type: '', message: '' }), 5000)
	}

	const handleUserRegister = async e => {
		e.preventDefault()
		setLoading(true)

		try {
			await userAPI.register(user)
			showAlert('success', "Muvaffaqiyatli ro'yxatdan o'tdingiz!")
			setUser({ full_name: '', room_number: '' })
		} catch (error) {
			showAlert('error', error.response?.data?.message || 'Xatolik yuz berdi')
		} finally {
			setLoading(false)
		}
	}

	const handleMachineSelect = machineId => {
		setSelectedMachines(prev => {
			if (prev.includes(machineId)) {
				return prev.filter(id => id !== machineId)
			} else if (prev.length < 2) {
				return [...prev, machineId]
			}
			return prev
		})
	}

	const handleBooking = async e => {
		e.preventDefault()

		if (!user.room_number) {
			showAlert('error', "Avval ro'yxatdan o'ting")
			return
		}

		if (!selectedTimeSlot) {
			showAlert('error', "Vaqt oralig'ini tanlang")
			return
		}

		if (selectedMachines.length === 0) {
			showAlert('error', 'Kamida bitta mashina tanlang')
			return
		}

		setLoading(true)

		try {
			const bookingData = {
				room_number: user.room_number,
				machine_ids: selectedMachines,
				booking_date: format(selectedDate, 'yyyy-MM-dd'),
				time_slot: selectedTimeSlot,
			}

			await bookingAPI.create(bookingData)
			showAlert(
				'success',
				`Muvaffaqiyatli bron qilindi! ${selectedMachines.length} ta mashina`
			)

			setSelectedMachines([])
			setSelectedTimeSlot('')
			loadBookings()
		} catch (error) {
			showAlert(
				'error',
				error.response?.data?.message || 'Bron qilishda xatolik'
			)
		} finally {
			setLoading(false)
		}
	}

	const isMachineBooked = (machineId, timeSlot) => {
		return bookings.some(
			booking =>
				booking.machine_id === machineId && booking.time_slot === timeSlot
		)
	}

	const isPastDate = date => {
		const today = new Date()
		today.setHours(0, 0, 0, 0)
		return isBefore(date, today)
	}

	const getWeekDates = () => {
		const start = startOfWeek(selectedDate, { weekStartsOn: 1 })
		const dates = []

		for (let i = 0; i < 5; i++) {
			dates.push(addDays(start, i))
		}

		return dates
	}

	return (
		<div className='container'>
			<div className='hero'>
				<h1>Kir Yuvish Mashinasi Bron Qilish</h1>
				<p>
					Xonangiz raqami bilan ro'yxatdan o'ting va mashinalarni bron qiling
				</p>
			</div>

			<Alert type={alert.type} message={alert.message} />

			{/* Ro'yxatdan o'tish formasi */}
			<div className='form-container'>
				<h2>Ro'yxatdan O'tish</h2>
				<form onSubmit={handleUserRegister}>
					<div className='form-group'>
						<label>Ismingiz</label>
						<input
							type='text'
							value={user.full_name}
							onChange={e => setUser({ ...user, full_name: e.target.value })}
							required
							placeholder="To'liq ismingiz"
						/>
					</div>
					<div className='form-group'>
						<label>Xona Raqami</label>
						<input
							type='text'
							value={user.room_number}
							onChange={e => setUser({ ...user, room_number: e.target.value })}
							required
							placeholder='Masalan: 101'
						/>
					</div>
					<button type='submit' className='btn' disabled={loading}>
						{loading ? 'Kutilmoqda...' : "Ro'yxatdan O'tish"}
					</button>
				</form>
			</div>

			{/* Kalendar */}
			<div className='form-container'>
				<h2>Kunni Tanlang</h2>
				<div className='calendar-grid'>
					{getWeekDates().map((date, index) => (
						<div
							key={index}
							className={`calendar-day ${
								format(date, 'yyyy-MM-dd') ===
								format(selectedDate, 'yyyy-MM-dd')
									? 'selected'
									: ''
							} ${isPastDate(date) ? 'disabled' : ''}`}
							onClick={() => !isPastDate(date) && setSelectedDate(date)}
						>
							<div>{format(date, 'EEE')}</div>
							<div>{format(date, 'dd')}</div>
							<div>{format(date, 'MMM')}</div>
						</div>
					))}
				</div>
			</div>

			{/* Bron qilish formasi */}
			{!isPastDate(selectedDate) && (
				<div className='form-container'>
					<h2>Mashina Bron Qilish</h2>
					<p>
						Tanlangan kun: <strong>{format(selectedDate, 'dd.MM.yyyy')}</strong>
					</p>

					<form onSubmit={handleBooking}>
						<div className='form-group'>
							<label>Vaqt Oralig'i</label>
							<select
								value={selectedTimeSlot}
								onChange={e => setSelectedTimeSlot(e.target.value)}
								required
							>
								<option value=''>Vaqtni tanlang</option>
								{timeSlots.map(slot => (
									<option key={slot} value={slot}>
										{slot}
									</option>
								))}
							</select>
						</div>

						<div className='booking-grid'>
							{timeSlots.map(timeSlot => (
								<div key={timeSlot} className='time-slot'>
									<h3>{timeSlot}</h3>
									<div className='machine-list'>
										{machines.map(machine => {
											const isBooked = isMachineBooked(machine.id, timeSlot)
											const isSelected =
												selectedMachines.includes(machine.id) &&
												selectedTimeSlot === timeSlot

											return (
												<div key={machine.id} className='machine-item'>
													<div className='machine-info'>
														<input
															type='checkbox'
															className='machine-checkbox'
															checked={isSelected}
															onChange={() => {
																if (
																	!isBooked &&
																	selectedTimeSlot === timeSlot
																) {
																	handleMachineSelect(machine.id)
																}
															}}
															disabled={
																isBooked || selectedTimeSlot !== timeSlot
															}
														/>
														<span>{machine.name}</span>
													</div>
													<div
														className={`machine-status ${
															isBooked ? 'status-booked' : 'status-available'
														}`}
													>
														{isBooked ? 'Band' : "Bo'sh"}
													</div>
												</div>
											)
										})}
									</div>
								</div>
							))}
						</div>

						<div style={{ marginTop: '1rem' }}>
							<p>Tanlangan mashinalar: {selectedMachines.length}/2</p>
						</div>

						<button
							type='submit'
							className='btn'
							disabled={
								loading || selectedMachines.length === 0 || !selectedTimeSlot
							}
							style={{ marginTop: '1rem' }}
						>
							{loading ? 'Bron qilinmoqda...' : 'Bron Qilish'}
						</button>
					</form>

					{user.room_number && (
						<div style={{ marginTop: '1rem', textAlign: 'center' }}>
							<a
								href={`/my-bookings/${user.room_number}`}
								className='btn'
								style={{ background: '#28a745' }}
							>
								Mening Bronlarim
							</a>
						</div>
					)}
				</div>
			)}
		</div>
	)
}

export default Home
