{
	machines.map(machine => {
		const isBooked = isMachineBooked(machine.id, selectedTimeSlot)
		const isSelected = selectedMachines.includes(machine.id)
		const isActive = machine.is_active // YANGI: Mashina faol yoki yo'qligini tekshirish

		return (
			<div key={machine.id} className='machine-item'>
				<div className='machine-info'>
					<input
						type='checkbox'
						className='machine-checkbox'
						checked={isSelected}
						onChange={() => {
							if (!isBooked && isActive) {
								// YANGI: Faol bo'lsagina tanlash mumkin
								handleMachineSelect(machine.id)
							}
						}}
						disabled={isBooked || !isActive} // YANGI: Faol bo'lmasa cheklangan
					/>
					<span style={{ fontWeight: '500' }}>{machine.name}</span>
					{!isActive && ( // YANGI: Agar mashina nofaol bo'lsa
						<span
							style={{
								color: '#dc3545',
								fontSize: '0.8rem',
								marginLeft: '0.5rem',
								fontStyle: 'italic',
							}}
						>
							(Ta'mirlanmoqda)
						</span>
					)}
				</div>
				<div
					className={`machine-status ${
						!isActive
							? 'status-booked'
							: isBooked
							? 'status-booked'
							: 'status-available'
					}`}
				>
					{!isActive ? 'Nofaol' : isBooked ? 'Band' : "Bo'sh"}
				</div>
			</div>
		)
	})
}
