import { addDays, format, isBefore, startOfWeek } from 'date-fns'
import { useEffect, useState } from 'react'
import { useTranslation } from 'react-i18next'
import '../App.css' // siz bergan CSS shu faylga kiritilgan bo'lishi kerak
import Alert from '../components/Alert'
import Register from '../components/Register'
import { bookingAPI, machineAPI } from '../services/api'

const Home = () => {
	const { t } = useTranslation()

	const [userRoom, setUserRoom] = useState(
		localStorage.getItem('userRoom') || ''
	)
	const [currentView, setCurrentView] = useState(
		userRoom ? 'booking' : 'register'
	)
	const [machines, setMachines] = useState([])
	const [bookings, setBookings] = useState([])
	const [selectedDate, setSelectedDate] = useState(new Date())
	const [selectedTimeSlot, setSelectedTimeSlot] = useState('')
	const [selectedMachines, setSelectedMachines] = useState([])
	const [alert, setAlert] = useState({ type: '', message: '' })
	const [loading, setLoading] = useState(false)

	const timeSlots = ['15:00-16:00', '16:00-17:00', '17:00-18:00']

	useEffect(() => {
		if (userRoom) {
			loadMachines()
			loadBookings()
		}
	}, [selectedDate, userRoom])

	const showAlert = (type, message) => {
		setAlert({ type, message })
		setTimeout(() => setAlert({ type: '', message: '' }), 5000)
	}

	const loadMachines = async () => {
		try {
			const res = await machineAPI.getAll()
			setMachines(res.data.data || [])
		} catch {
			showAlert('error', 'Mashinalarni yuklashda xatolik')
		}
	}

	const loadBookings = async () => {
		try {
			const dateStr = format(selectedDate, 'yyyy-MM-dd')
			const res = await bookingAPI.getAll(dateStr)
			setBookings(res.data.data || [])
		} catch {
			showAlert('error', 'Bronlarni yuklashda xatolik')
		}
	}

	const handleRegister = roomNumber => {
		setUserRoom(roomNumber)
		localStorage.setItem('userRoom', roomNumber)
		setCurrentView('booking')
		showAlert('success', t('register.success'))
	}

	const handleLogout = () => {
		setUserRoom('')
		localStorage.removeItem('userRoom')
		setCurrentView('register')
		setSelectedMachines([])
		setSelectedTimeSlot('')
	}

	const handleMachineSelect = machineId => {
		setSelectedMachines(prev => {
			if (prev.includes(machineId)) return prev.filter(id => id !== machineId)
			if (prev.length < 2) return [...prev, machineId]
			return prev
		})
	}

	const handleBooking = async e => {
		e.preventDefault()
		if (!selectedTimeSlot) {
			showAlert('error', t('booking.chooseTime'))
			return
		}
		if (selectedMachines.length === 0) {
			showAlert('error', 'Kamida bitta mashina tanlang')
			return
		}

		setLoading(true)
		try {
			const bookingData = {
				room_number: userRoom,
				machine_ids: selectedMachines,
				booking_date: format(selectedDate, 'yyyy-MM-dd'),
				time_slot: selectedTimeSlot,
			}
			await bookingAPI.create(bookingData)
			showAlert(
				'success',
				`Muvaffaqiyatli bron qilindi! ${selectedMachines.length} ta mashina`
			)
			setSelectedMachines([])
			setSelectedTimeSlot('')
			loadBookings()
		} catch {
			showAlert('error', 'Bron qilishda xatolik')
		} finally {
			setLoading(false)
		}
	}

	const isMachineBooked = (machineId, timeSlot) =>
		bookings.some(b => b.machine_id === machineId && b.time_slot === timeSlot)
	const isPastDate = date => {
		const today = new Date()
		today.setHours(0, 0, 0, 0)
		return isBefore(date, today)
	}
	const getWeekDates = () => {
		const start = startOfWeek(selectedDate, { weekStartsOn: 1 })
		return Array.from({ length: 5 }, (_, i) => addDays(start, i))
	}

	if (currentView === 'register') {
		return (
			<div className='admin-login'>
				<Register onRegister={handleRegister} />
				<Alert type={alert.type} message={alert.message} />
			</div>
		)
	}

	return (
		<div className='admin-container'>
			<header className='admin-header'>
				<h1>{t('home.booking')}</h1>
				<button onClick={handleLogout} className='btn-danger logout-btn'>
					{t('admin.logout')}
				</button>
			</header>

			<Alert type={alert.type} message={alert.message} />

			{/* Kalendar */}
			<section>
				<label>{t('booking.selectDate')}</label>
				<div className='calendar-grid'>
					{getWeekDates().map((date, idx) => (
						<div
							key={idx}
							className={`calendar-day ${format(date, 'yyyy-MM-dd') === format(selectedDate, 'yyyy-MM-dd') ? 'selected' : ''} ${isPastDate(date) ? 'disabled' : ''}`}
							onClick={() => {
								if (!isPastDate(date)) {
									setSelectedDate(date)
									setSelectedTimeSlot('')
									setSelectedMachines([])
								}
							}}
						>
							<div>{format(date, 'EEE')}</div>
							<div>{format(date, 'dd')}</div>
							<div>{format(date, 'MMM')}</div>
						</div>
					))}
				</div>
			</section>

			{/* Mashinalar */}
			{!isPastDate(selectedDate) && (
				<section>
					<form onSubmit={handleBooking}>
						<div className='form-group'>
							<label>{t('booking.selectTime')}</label>
							<select
								value={selectedTimeSlot}
								onChange={e => {
									setSelectedTimeSlot(e.target.value)
									setSelectedMachines([])
								}}
								required
							>
								<option value=''>{t('booking.chooseTime')}</option>
								{timeSlots.map(slot => (
									<option key={slot} value={slot}>
										{slot}
									</option>
								))}
							</select>
						</div>

						{selectedTimeSlot && (
							<div className='machine-list'>
								{machines.map(machine => {
									const isBooked = isMachineBooked(machine.id, selectedTimeSlot)
									const isSelected = selectedMachines.includes(machine.id)
									const isActive = machine.is_active

									return (
										<div key={machine.id} className='machine-item'>
											<input
												type='checkbox'
												checked={isSelected}
												disabled={isBooked || !isActive}
												onChange={() => handleMachineSelect(machine.id)}
											/>
											<span>{machine.name}</span>
											{!isActive && (
												<span className='maintenance'>
													({t('booking.underMaintenance')})
												</span>
											)}
											<span
												className={`machine-status ${!isActive ? 'status-booked' : isBooked ? 'status-booked' : 'status-available'}`}
											>
												{!isActive
													? t('booking.inactive')
													: isBooked
														? t('booking.busy')
														: t('booking.available')}
											</span>
										</div>
									)
								})}
							</div>
						)}

						<div className='selected-summary'>
							<p>
								{t('booking.selectedMachines')}:{' '}
								<strong>{selectedMachines.length}/2</strong>
							</p>
						</div>

						<button
							type='submit'
							className='btn btn-success'
							disabled={
								loading || selectedMachines.length === 0 || !selectedTimeSlot
							}
						>
							{loading ? t('booking.booking') : t('booking.book')}
						</button>
					</form>
				</section>
			)}
		</div>
	)
}

export default Home
