import { useTranslation } from 'react-i18next'
import { Link } from 'react-router-dom'

const Header = () => {
	const { t, i18n } = useTranslation()
	const userRoom = localStorage.getItem('userRoom')

	const changeLanguage = lng => {
		i18n.changeLanguage(lng)
	}

	const buttonStyle = {
		background: 'transparent',
		color: '#fff',
		border: '1px solid #fff',
		padding: '0.25rem 0.8rem',
		borderRadius: '4px',
		cursor: 'pointer',
		fontSize: '0.8rem',
		transition: 'all 0.3s',
	}

	const activeButtonStyle = {
		background: '#fff',
		color: '#667eea',
		border: '1px solid #fff',
		padding: '0.25rem 0.8rem',
		borderRadius: '4px',
		cursor: 'pointer',
		fontSize: '0.8rem',
		transition: 'all 0.3s',
	}

	const navLinkStyle = {
		color: 'white',
		textDecoration: 'none',
		marginLeft: '1rem',
		padding: '0.4rem 0.8rem',
		border: '1px solid rgba(255,255,255,0.3)',
		borderRadius: '4px',
		transition: 'all 0.3s',
		background: 'rgba(255,255,255,0.1)',
	}

	const navLinkHoverStyle = {
		background: 'rgba(255,255,255,0.2)',
		borderColor: 'rgba(255,255,255,0.5)',
	}

	return (
		<header className='header'>
			<div className='container'>
				<div className='header-content'>
					<div className='logo'>{t('header.title')}</div>
					<nav className='nav'>
						<Link
							to='/'
							style={navLinkStyle}
							onMouseOver={e =>
								Object.assign(e.target.style, navLinkHoverStyle)
							}
							onMouseOut={e => Object.assign(e.target.style, navLinkStyle)}
						>
							{t('header.home')}
						</Link>
						<Link
							to='/admin'
							style={navLinkStyle}
							onMouseOver={e =>
								Object.assign(e.target.style, navLinkHoverStyle)
							}
							onMouseOut={e => Object.assign(e.target.style, navLinkStyle)}
						>
							{t('header.admin')}
						</Link>

						{/* Til almashtirish tugmalari */}
						<div style={{ display: 'flex', gap: '0.5rem', marginLeft: '1rem' }}>
							<button
								onClick={() => changeLanguage('uz')}
								style={i18n.language === 'uz' ? activeButtonStyle : buttonStyle}
							>
								UZ
							</button>
							<button
								onClick={() => changeLanguage('en')}
								style={i18n.language === 'en' ? activeButtonStyle : buttonStyle}
							>
								EN
							</button>
							<button
								onClick={() => changeLanguage('ru')}
								style={i18n.language === 'ru' ? activeButtonStyle : buttonStyle}
							>
								RU
							</button>
						</div>
					</nav>
				</div>
			</div>
		</header>
	)
}

export default Header
